$(document).ready(function(){
    $(".fa-bars").on("click",function(){
        $("#side-menu").toggleClass("toggle-side"),
        $("#full-page").toggleClass("toggle-page");
    });
});

$(document).ready(function(){
    $('#close-btn').on('click',function(){
        $("#side-menu").toggleClass("toggle-side"),
        $("#full-page").toggleClass("toggle-page")
    })
})